function normalizeHinglish(text) {
  return text
    .replace(/\b(mai|mei|mein|me)\b/gi, "main")
    .replace(/\b(ko|ke)\b/gi, "")
    .replace(/\b(karo?ungi|karoongee|karoongi|karonga|karoonga|karunga)\b/gi, "karunga")
    .replace(/\b(du?ngi|doongi|doongii|doongee|dunga|doonga)\b/gi, "dunga")
    .replace(/\b(bharo?ungi|bharoongee|bharoongi|bharunga|bharoonga)\b/gi, "bharunga")
    .replace(/\b(bharu?nga|bhardoonga|bhar dunga|bharenge|bharoon|bharne wala)\b/gi, "bharunga")
    .replace(/\b(dedoonga|dedonga|de dunga|dunga|doonga|deunga)\b/gi, "dunga")
    .replace(/\b(settle karungi|settle karoongi|settle karonga|settle karoonga)\b/gi, "settle karunga")
    .replace(/\b(clear karungi|clear karoongi|clear karonga|clear karoonga)\b/gi, "clear karunga")
    .replace(/\b(ho)\b/gi, "hoon")
    .replace(/\b(paise|paisa|amount|due)\b/gi, "paisa")
    .replace(/\s+/g, " ")
    .trim();
}

