// --- Mode Extraction ---
const modeKeywords = {
  upi: /\b(upi|gpay|google\s*pay|phonepe|paytm)\b/i,
  cash: /\bcash\b/i
};

function extractMode(text) {
  if (modeKeywords.upi.test(text)) return "UPI";
  if (modeKeywords.cash.test(text)) return "cash";
  return "";
}

// --- Amount Extraction ---
function extractAmount(text) {
  const minDue = $('If1').first().json.min_due;
  const amountMatches = text.matchAll(/(?:rs\.?|‚Çπ)\s?(\d{2,7})\b/gi);
  const fallbackMatch = text.match(/(?:‡Æ™‡Æ£‡ÆÆ‡Øç|‡Æö‡ØÜ‡Æ≤‡ØÅ‡Æ§‡Øç‡Æ§‡ØÅ\w*|‡Æï‡Æü‡Øç‡Æü‡Æ£‡ÆÆ‡Øç)\s*(\d{2,7})/i);
  if (fallbackMatch) return fallbackMatch[1];
  for (const match of amountMatches) {
    const amt = match[1];
    const val = parseInt(amt.replace(/,/g, ""));
    if (val > 99) return String(val); // pick first valid amount > 99
  }
  return minDue ? String(minDue) : "";
}

// --- Date Extraction (unchanged) ---
function getDaysToEndOfWeek(date) {
  const day = date.getDay(); // 0 (Sun) to 6 (Sat)
  const dayIndex = day === 0 ? 7 : day; // treat Sunday as 7
  return 7 - dayIndex;
}


