    .replace(/\b(settle karungi|settle karoongi|settle karonga|settle karoonga)\b/gi, "settle karunga")
    .replace(/\b(clear karungi|clear karoongi|clear karonga|clear karoonga)\b/gi, "clear karunga")
    .replace(/\b(ho)\b/gi, "hoon")
    .replace(/\b(paise|paisa|amount|due)\b/gi, "paisa")
    .replace(/\s+/g, " ")
    .trim();
}


