// --- WhatsApp Message Info ---
const rawText = $('WhatsApp Trigger').first().json.messages[0].text.body?.toLowerCase() || "";

// --- Normalize Common Typos ---
function normalizeTypos(text) {
  return text
    .replace(/\bthanx\b/gi, "thanks")
    .replace(/\bthnx\b/gi, "thanks")
    .replace(/\bthnks\b/gi, "thanks")
    .replace(/\bthanku\b/gi, "thank you")
    .replace(/\bgn\b/gi, "good night")
    .replace(/\bbye+e*\b/gi, "bye")
    .replace(/\bokay\b/gi, "ok")
    .replace(/\bok\b/gi, "ok")
    .replace(/\bokk+\b/gi, "ok")
    .replace(/\bsahi\b/gi, "ok")
    .replace(/\btata\b/gi, "bye")
    .replace(/\bchalo\b/gi, "bye")
    .replace(/\bacha\b/gi, "ok")
    .replace(/\bacha theek\b/gi, "ok")
    .replace(/\balright\b/gi, "ok")
    .replace(/\s+/g, " ")
    .trim();
}

// --- Normalize Hinglish / Code-Mixed ---
function normalizeHinglish(text) {
  return text
    .replace(/\b(shukriya|dhanyavad|dhanyvaad)\b/gi, "thank you")
    .replace(/\b(chalta hoon|nikalta hoon|nikal raha hoon)\b/gi, "bye")
    .replace(/\b(theek hai|thik hai|tik hai|sahi hai)\b/gi, "ok")
    .replace(/\b(bye bye|bye-bye)\b/gi, "bye")
    .replace(/\b(ho gaya|done ho gaya|bas itna)\b/gi, "done")
    .replace(/\s+/g, " ")
    .trim();
}

// --- Levenshtein Distance Fuzzy Matcher ---
function fuzzyIncludes(input, variants, maxDistance = 2) {
  function levenshtein(a, b) {
    const matrix = Array.from({ length: b.length + 1 }, (_, i) => [i]);
    for (let j = 0; j <= a.length; j++) matrix[0][j] = j;
    for (let i = 1; i <= b.length; i++) {
      for (let j = 1; j <= a.length; j++) {
        const cost = b[i - 1] === a[j - 1] ? 0 : 1;
        matrix[i][j] = Math.min(
          matrix[i - 1][j] + 1,     // deletion
          matrix[i][j - 1] + 1,     // insertion
          matrix[i - 1][j - 1] + cost // substitution
        );
      }
    }
    return matrix[b.length][a.length];
  }

  return variants.some(variant => levenshtein(input, variant) <= maxDistance);
}

// --- Process Text ---
const text = normalizeHinglish(normalizeTypos(rawText));

// --- Keywords List (English + Hinglish + Multilingual) ---
const resetKeywords = [
  "thanks", "thank you", "bye", "done", "finished", "all good", "talk later",
  "à¤¬à¤¾à¤¯", "à¤§à¤¨à¥à¤¯à¤µà¤¾à¤¦", "à¤¶à¥à¤•à¥à¤°à¤¿à¤¯à¤¾", "à¤ à¥€à¤• à¤¹à¥ˆ", "à¤¹à¥‹ à¤—à¤¯à¤¾", "à¤¸à¤®à¤¾à¤ªà¥à¤¤", "à¤§à¤¨à¥à¤¯", "à¤šà¤²à¥‡à¤—à¤¾", "à¤šà¤²à¥‡", 
  "à¤—à¥à¤¡ à¤¨à¤¾à¤‡à¤Ÿ", "à¤…à¤²à¤µà¤¿à¤¦à¤¾", "à¤¨à¤®à¤¸à¥à¤¤à¥‡", "à¤¥à¥ˆà¤‚à¤• à¤¯à¥‚", "à¤šà¤²à¤¾ à¤œà¤¾à¤¤à¤¾ à¤¹à¥‚à¤", "à¤šà¤²à¥€ à¤œà¤¾à¤¤à¥€ à¤¹à¥‚à¤", 
  "à®®à¯à®Ÿà®¿à®¨à¯à®¤à®¤à¯", "à®¨à®©à¯à®±à®¿", "à®šà¯†à®²à¯à®•à®¿à®±à¯‡à®©à¯", // Tamil
  "à°§à°¨à±à°¯à°µà°¾à°¦à°¾à°²à±", "à°¬à±ˆ", "à°ªà±‹à°¯à°¾à°¨à±", "à°šà°¾à°²à°¿à°‚à°¦à°¿", "à°¸à°°à±‡", // Telugu
  "à¤§à¤¨à¥à¤¯à¤µà¤¾à¤¦", "à¤à¤¾à¤²à¥‡", "à¤ à¤¿à¤• à¤†à¤¹à¥‡", "à¤šà¤¾à¤²à¥‡à¤²", "à¤¬à¤¾à¤¯", "à¤—à¥à¤¡ à¤¨à¤¾à¤ˆà¤Ÿ" // Marathi
];

// --- Reset Detection ---
const shouldReset = resetKeywords.some(keyword => text.includes(keyword)) ||
                    fuzzyIncludes(text, resetKeywords, 2);

// --- Final Output ---
return {
  json: {
    shouldReset
  }
};
